var exports = module.exports = {};

// Global delte object function
exports.deleteObject = function(req, col, query, callback) {
    var db = req.db;
    var collection = db.get(col);
    collection.remove(query, function(err, response){
        callback((err === null) ? { msg: 'Deleted ' + col, records_deleted: response } : { msg:'error: ' + err });
    });
}